#!/bin/sh# You may use, modify and/or distribute under the terms of the GNU General # Public License.# Please see the LICENSE file for the specific terms of the license.## Automatically created player patches from a template file## pix@test.at -inital version# nik@f0.am - modified + new templatesif [ -z "$1" ] ; then  echo "usage: $0 <player_number>"else  for i in dynamics munge shell route_input route_output state_topology toOSC-state; do    perl -pe "s/\[p\]/$1/g" < $i.template > p$1-$i    donefi